//IMPORTACIONES
import { Container,Navbar,Nav,Form ,FormControl,Button} from 'react-bootstrap';
import { NavLink, useParams } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';

export default function Barra() {
  
    //SACANDO LOS PARAMENTROS DE LA URL
    const {tipoGrafica}=useParams();
    //ESTADO PARA MANEJAR EL DATO DEL SEARCH
    const [searchUser,setSearchUser]=useState('');


    const handleSubmitSearchUser= async(e) => { 

      e.preventDefault();
      try {

        //PIDIENDO LOS DATOS DEL USUARIO
        const searchUser= await axios.get(`http://localhost:3006/Users/usersSearch/${searchUser}`,{

            headers:{

              'access-token':sessionStorage.getItem('token')

            }

        })


        console.log(searchUser.data);


      } catch (error) {
          
      }


     }

  
  return (
    <Navbar expand="lg" bg="dark" variant="dark">
    <Container >
    
    <NavLink to={`/Dashboard/${tipoGrafica}/usersTableList`} className='navbar-brand'>Users Manage</NavLink>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        
        <Nav className="me-auto">
          <NavLink  className='nav-link' to={`/Dashboard/${tipoGrafica}/CreateUsersForm`}>Create Users</NavLink>
          <NavLink  className='nav-link' to={`/Dashboard/${tipoGrafica}/DeleteUsersForm`}>Delete Users</NavLink>
          <NavLink  className='nav-link' to={`/Dashboard/${tipoGrafica}/UpdateUserForm`}>Actualizar Users</NavLink>
        </Nav>
        <Form className="d-flex" onSubmit={handleSubmitSearchUser}>
        <FormControl
          type="search"
          placeholder="Buscar Usuario"
          className="me-2"
          aria-label="Search"
          required
          value={searchUser}
          onChange={e=>setSearchUser(e.target.value)}
        />
        <Button variant="outline-light">Buscar</Button>
      </Form>
      </Navbar.Collapse>
    </Container>
  </Navbar>
  )
}
